mapper.select:
/* kr.letech.study.cmmn.user.dao.UserDAO.selectUserList */ SELECT USER_ID AS userId , USER_NM AS userNm , RGST_ID AS rgstId , DATE_FORMAT(RGST_DT, '%Y-%m-%d %H:%i:%s') AS rgstDt FROM TB_USER WHERE DEL_YN = 'N'
AND USER_ID LIKE CONCAT('%', #{userId}, '%')
AND USER_NM LIKE CONCAT('%', #{userNm}, '%')

mapper.insert:
/* kr.letech.study.cmmn.user.dao.UserDAO.insertUser */
INSERT INTO TB_USER
     (
       USER_ID
	 , USER_PW
	 , USER_NM
	 , PHT_FILE_GRP_ID
	 , RGST_ID
	 , RGST_DT
	 , UPDT_ID
	 , UPDT_DT
	 , DEL_YN
     )
       VALUES
     (
       #{userId}
	 , #{userPw}
	 , #{userNm}
	 , #{phtFileGrpId}
	 , #{rgstId}
	 , NOW()
	 , #{updtId}
	 , NOW()
	 , 'N'
     )

mapper.update:
/* kr.letech.study.cmmn.user.dao.UserDAO.updateUser */
UPDATE TB_USER
   SET USER_NM = #{userNm}

     , USER_PW = #{userPw}

     , UPDT_ID = #{updtId}
	 , UPDT_DT = NOW()
 WHERE USER_ID = #{userId}

/* kr.letech.study.cmmn.user.dao.UserDAO.deleteUser */
UPDATE TB_USER
   SET DEL_YN  = 'Y'
     , UPDT_ID = #{updtId}
	 , UPDT_DT = NOW()
 WHERE USER_ID = #{userId}
